import React, { useState, useEffect } from "react";

const API_URL = "http://localhost:8000"; // backend url

const TaskList = () => {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState({ title: "", description: "", status: false });

  // Fetch all tasks
  const fetchTasks = async () => {
    try {
      const response = await fetch(`${API_URL}/tasks`);
      const data = await response.json();
      setTasks(data);
    } catch (error) {
      console.error("Error fetching tasks:", error);
    }
  };

  // Create a new task
  const handleCreate = async () => {
    if (!newTask.title || !newTask.description) {
      alert("Title and Description are required!");
      return;
    }
  
    try {
      const response = await fetch(`${API_URL}/newtasks`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          title: newTask.title,
          description: newTask.description,
          status: newTask.status ?? false, // Ensure status is always sent
        }),
      });
  
      if (response.ok) {
        fetchTasks(); // Refresh task list
        setNewTask({ title: "", description: "", status: false }); // Reset input
      }
    } catch (error) {
      console.error("Error adding task:", error);
    }
  };
  
  // Update task status
  const handleUpdate = async (id) => {
    const taskToUpdate = tasks.find((t) => t.id === id);
    if (!taskToUpdate) return;
  
    try {
      await fetch(`${API_URL}/updatetasks/${id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          title: taskToUpdate.title, 
          description: taskToUpdate.description, 
          status: true, // Mark as completed
        }),
      });
  
      fetchTasks(); // Refresh task list
    } catch (error) {
      console.error("Error updating task:", error);
    }
  };
  
  // Delete task
  const handleDelete = async (id) => {
    try {
      await fetch(`${API_URL}/deltasks/${id}`, {
        method: "DELETE",
      });

      fetchTasks(); // Refresh task list
    } catch (error) {
      console.error("Error deleting task:", error);
    }
  };

  // Load tasks on component mount
  useEffect(() => {
    fetchTasks();
  }, []);

  return (
    <div>
      <h2>Task Manager</h2>
      <input
        type="text"
        placeholder="Task Title"
        value={newTask.title}
        onChange={(e) => setNewTask({ ...newTask, title: e.target.value })}
      />
      <input
        type="text"
        placeholder="Task Description"
        value={newTask.description}
        onChange={(e) => setNewTask({ ...newTask, description: e.target.value })}
      />
      <button onClick={handleCreate}>Add Task</button>

      {/* <ul>
        {tasks.map((task) => (
          <li key={task.id}>
            {task.title} - {task.status ? "✅ Completed" : "⏳ Pending"}
            <button onClick={() => handleUpdate(task.id)}>Complete</button>
            <button onClick={() => handleDelete(task.id)}>Delete</button>
          </li>
        ))}
      </ul> */}
    </div>
  );
};

export default TaskList;
